def.body:
@mutex.synchronize do
  if @entries[@head] < 1.minute.ago
    @entries[@head] = Time.utc
    @head = (@head + 1) % 100
    return
  else
    sleep_time = (60.seconds - (Time.utc - @entries[@head])) + @backoff_time.seconds
    Log.warn do
      "Hit rate limit while attempting request to #{endpoint}. Pausing requests for #{sleep_time.total_seconds} seconds."
    end
    sleep(sleep_time.total_seconds)
    @entries[@head] = Time.utc
    @head = (@head + 1) % 100
    return
  end
end

if @rel == "user"
  return Srcom::Api::Users.find_by_id(@id.not_nil!)
else
  if (  name = @name)
    return Srcom::Api::Guests.find_by_name(name)
  else
    return Srcom::Guest.new
  end
end
